  background-position: center;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
}
.text {
  width: 100%;
  max-width: 650px;
  margin-bottom: 100px;
}

@keyframes town {
  from {
    transform: translateX(-30px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}
@keyframes country {
  from {
